import React, { useState } from 'react';
import Header from './Header/index';
import Footer from './Footer/index';
import './allcomponentindex.css';
import headerimg from './Header/header.png';
import Range from './Range/index';
import Histogram from './Histogram/index';
import CreditLimit from './CreditLimit/index';
import HeatMap from './HeatMap/index';

function hover(e) {
    e.target.style.background='darkcyan';
}
function hoverout(e){
    e.target.style.background='none';
}

function Components() {
    const [selected,setselected] = useState("address");
    const accountinfoset=[
        {accId:1234273637,accCompCode:999,accNum:123,type:"Savings"},
        {accId:7454367373,accCompCode:989,accNum:125,type:"Checkings"}
    ];

    function AccountDetailsInfo(accval) {
        return(
            <div className="accountdiv">
                                        <div className="accountLabel">Account Id: {accval.accId}</div>
        <div className="accountLabel">Account Company Code: {accval.accCompCode}</div>
        <div className="accountLabel">Account Number: {accval.accNum}</div>
        <div className="accountLabel">Account Type: {accval.type}</div>

                                    </div>
        );
    }

    return(
        <div className="DisplayMain">
           <div className="HeaderDiv">
           <img className="headerImage" src={headerimg} alt="Wells Fargo Logo"/>
        </div>
            <div className="ContentDiv">
                <div className="TopRow">
                    <div className="IdDiv">
                       <div> <b>Customer Number:</b> 1448536655477<br/> </div>
                       <div><b> Custmer Id: </b>121</div>
                       <div><b>Customer Tie Breaker: </b>1</div>

                    </div>
                    <div className="BasicDetails">
                        <div><b>Name:</b>John Doe</div>
                        <div><b>DOB:</b>01/01/1234</div>
                    </div>
                </div>
                <div className="ContentRow">
                <div className="SideMenu" >

                        <ul className="sideMenuList" >
                            <li className="ListTabs" onMouseEnter={hover} onMouseLeave={hoverout} onClick={()=>setselected("address")}>Address Details</li>
                            <li className="ListTabs" onMouseEnter={hover} onMouseLeave={hoverout} onClick={()=>setselected("accountsInfo")}>Accounts Information</li>
                            <li className="ListTabs" onMouseEnter={hover} onMouseLeave={hoverout} onClick={()=>setselected("riskIndicator")}>Risk Indicator</li>
                            <li className="ListTabs" onMouseEnter={hover} onMouseLeave={hoverout} onClick={()=>setselected("historical")}>Transition History</li>
                            <li className="ListTabs" onMouseEnter={hover} onMouseLeave={hoverout} onClick={()=>setselected("creditutilization")}>Credit Limit Utilization</li>
                            <li className="ListTabs" onMouseEnter={hover} onMouseLeave={hoverout} onClick={()=>setselected("accountdetails")}>Account History Details</li>
                        </ul>


                    </div>
                    <div className="ContentDetails">
                    {selected==="address" &&
                    <div>
                        <div className="content"><b>Address: </b>B12/542 Charle's Street</div>
                        <div className="content"><b>City: </b>New York</div>
                        <div className="content"><b>Country: </b>US</div>    
                        </div>            
                    }
                    {selected==="riskIndicator" &&
                         <div className="contentrisk"><div className="labeldiv"><b>Risk Indicator: </b></div><div className="rangemaindiv"><Range/></div></div>
                    }
                    {selected==="historical" &&
                        <div className="histogramcontent">
                        <Histogram />
                        </div>
                    }
                    {selected==="creditutilization" &&
                        <div className="creditlimit">
                            <CreditLimit />
                            </div>

                    }
                    {
                        selected==="accountdetails" &&
                        <div className="accountdetailsdiv">                   


                            <HeatMap />
                            </div>
                    }
                    {
                        selected==="accountsInfo" &&
                        <div className="accountsInfodiv">
                            <ol className="accountsList">
                                {accountinfoset.map((el,i)=>(
                                    <li className="account">
                                    {AccountDetailsInfo(el)}
                                </li>
                                ))}


                            </ol>
                            </div>
                    }

                     </div>
                </div>

            </div>
            <div className="FooterDiv">
            <b><h1>WELLS Fargo</h1></b><h5> All copyrights reserved.</h5>
        </div>
        </div>

    );
}

export default Components;
