import React from 'react';
import '../allcomponentindex.css'

class Histogram extends React.Component{
    state={
        Hist:[
            {
                month:"jan",
                bal:"20"
            },
            {
                month:"feb",
                bal:"40"
            },
            {
                month:"march",
                bal:"45"
            }
        ]
    }


    render(){

        const Line=({left}) =>{
                return(
                <div className="line" style={{left:`${left}%`}} >{left}</div>
                )
        }
        const Bar =({percent})=>{
            return(
                <div className="bar" style={{width:`${percent}%`}} />
            )
        }
        const BarTextContent=({history})=>{

            return(
                <div className="bartextcontent">
                   { history.map((month)=>(
                        <div className="text">
                            {month.month}
                        </div>
                    ))
                   }
                </div>
            )
        }
        const renderLines=()=>{
            return Array(10).fill(null).map((el,i)=>(
            <Line left={i*10} key={i} ></Line>
            ))
        }
        const renderNars=()=>{
            return(

                <div></div>
            )
        }
        const Markers = () => {
            const markerArr = Array(11).fill(null);

            return (
              <div className="markers">
                {
                  markerArr.map((el, i) => (
                   <div className="marker" style={{ left: `${i*10}%` }}>
                    { i * 10 }
                   </div>
                  ))
                }
              </div>
            )
          }



        return(

                <div className="graph">

                   <BarTextContent history={this.state.Hist}/>
                   <div className="graphfirst">
                    <div className="barLinesContainer">
                    {renderLines()}
                    <Bar percent={20} />
                    <Bar percent={40} />
                    <Bar percent={45} />
                    </div>

                    </div>

                </div>


        )
    }

}

export default Histogram;
