import React from 'react';
import '../allcomponentindex.css'

class Histogram extends React.Component{

    state={
        numMonths:0,
        rangeVal:0
    }

    render(props){

        const Line=({left}) =>{
                return(
                    <div className="line" style={{left:`${left}%`}}>
                </div>
                )
        }

        const Numbers=({left}) =>{
            return(
                <div className="linenum" style={{left:`${left}%`}}>
                    {left}
                </div>
            )
        }

        const Bar =({element})=>{
            return(
                <div className="bar" style={{width:`${element.transNum}%`}} />
            )
        }

        function decodeMonth({param}){
            switch(param){
                case '1': return "January";
                case 2: return "February";
                case 3: return "March";
                case 4: return "April";
                case 5: return "May";
                case 6: return "June";
                case 7: return "July";
                case 8: return "August";
                case 9: return "September";
                case 10: return "October";
                case 11: return "November";
                case 12: return "December";

            }
        }

        const BarTextContent=({history})=>{

            return(
                <div className="bartextcontent">
                   { history.map((month)=>(
                        <div className="text">
                            {
                            decodeMonth(month.transMonth)
                            }



                        </div>
                    ))
                   }
                </div>
            )
        }
        const renderLines=()=>{
            return Array(11).fill(null).map((el,i)=>(
                <div className="lineswdnumdiv">
            <Line left={i*10} key={i} ></Line>
            <Numbers left={i*10} key={i}></Numbers>
            </div>
            ))
        }



        return(

                <div className="graph">
                   <BarTextContent history={this.props.hist}/>
                   <div className="graphfirst">
                    <div className="barLinesContainer">
                    {renderLines()}
                    {this.props.hist.map((el,i)=>(
                                     <Bar element={el} />
                                ))}
                    </div>

                    </div>

                </div>


        )
    }

}

export default Histogram;
