import React, { useState } from 'react';
import './style.css';
import headerimg from '../Header/header.png';

function hover(e) {
    e.target.style.background='darkcyan';
}
function hoverout(e){
    e.target.style.background='none';
}


const AccountComponent = (props)=> {
    const [selected,setselected] = useState("address");
    const accountinfoset=[
        {accId:1234273637,accCompCode:999,accNum:123,type:"Loan",bal:547800,currencycode:"$"},
    ];
    return(
        <div className="DisplayMain">
           <div className="HeaderDiv">
           <img className="headerImage" src={headerimg} alt="Wells Fargo Logo"/>
        </div>
            <div className="ContentDiv">
                <div className="TopRow">
                    <div className="IdDiv">
                       <div> <b>Account Id:</b> 1448536655477<br/> </div>
                       <div><b>Account Company Code: </b>999</div>
                       <div><b>Account Number: </b>989</div>

                    </div>
                    <div className="BasicDetails">
                        <div><b>Account Type:</b>Savings</div>
                        <div><b>Balance:</b>1276$</div>
                    </div>
                </div>
                <div className="ContentRow">
                <div className="SideMenu" >

                        <ul className="sideMenuList" >
                            <li className="ListTabs" onMouseEnter={hover} onMouseLeave={hoverout} onClick={()=>setselected("address")}>Account Address</li>
                            <li className="ListTabs" onMouseEnter={hover} onMouseLeave={hoverout} onClick={()=>setselected("relatedAccounts")}>Related Accounts</li>

                        </ul>


                    </div>
                    <div className="ContentDetails">
                    {
                        selected==="address" &&
                    <div>
                        <div className="content"><b>Address: </b>B12/542 Charle's Street</div>
                        <div className="content"><b>City: </b>New York</div>
                        <div className="content"><b>Country: </b>US</div>    
                        </div>            
                        }
                </div>

                {
                        selected==="relatedAccounts" &&
                        <div className="accountsInfodiv">
                            <ol className="accountsList">
                                {accountinfoset.map((el,i)=>(
                                    <li className="account" key={i}>
                                    {AccountDetailsInfo(el)}
                                </li>
                                ))}


                            </ol>

                            </div>

                    }
                </div>

            </div>
            <div className="FooterDiv">
            <b><h1>WELLS Fargo</h1></b><h5> All copyrights reserved.</h5>
        </div>
        </div>

    );
}

export default AccountComponent;
