import React from 'react';
import '../allcomponentindex.css'

class Histogram extends React.Component{
    state={
        Hist:[
            {
                month:"jan",
                bal:"20"
            },
            {
                month:"feb",
                bal:"40"
            },
            {
                month:"march",
                bal:"45"
            }
        ]
    }


    render(){

        const Line=({left}) =>{
                return(
                    <div className="line" style={{left:`${left}%`}} />
                )
        }
        const Bar =({percent})=>{
            return(
                <div className="bar" style={{width:`${percent}%`}} />
            )
        }
        const BarTextContent=({Hist})=>{
            return(
                <div className="bartextcontent">
                    Hist.map((mon)=>(
                        <div className="text">
                            {mon.month}
                        </div>
                    ))
                </div>
            )
        }
        const renderLines=()=>{
            return Array(10).fill(null).map((el,i)=>(
                <Line left={i*10} key={i} />
            ))
        }
        const renderNars=()=>{
            return(

                <div></div>
            )
        }



        return(

                <div className="graph">
                   <BarTextContent />
                    <div className="barLinesContainer">
                    {renderLines()}
                    <Bar percent={10} />
                    <Bar percent={20} />
                    </div>
                </div>


        )
    }

}

export default Histogram;
